import React, { useState } from 'react';
import { FaStar } from 'react-icons/fa';


const Contact = () => {

    const [rating, setRating] = useState(null);
    const [hover, setHover] = useState(null);

  return (
    <div className="body max-w-md mx-auto my-4 p-6 bg-white rounded-md shadow-md">
      <h2 className="text-2xl font-bold mb-4">Contact Us</h2>
      <form>
        <div className="mb-4">
          <label htmlFor="name" className="block text-gray-700 font-bold mb-2">
            Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            className="border rounded-md py-2 px-3 w-full"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="email" className="block text-gray-700 font-bold mb-2">
            Email
          </label>
          <input
            type="email"
            id="email"
            name="email"
            className="border rounded-md py-2 px-3 w-full"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="message" className="block text-gray-700 font-bold mb-2">
            Message
          </label>
          <textarea
            id="message"
            name="message"
            className="border rounded-md py-2 px-3 w-full h-32 resize-none"
          ></textarea>
        </div>


        <div className="justify-center">
      {[...Array(5)].map((star, index) => {
        const ratingValue = index + 1;

        return (
          <label key={ratingValue}>
            <input
            className='appearance-none '
              type="radio"
              name="rating"
              value={ratingValue}
              onClick={() => setRating(ratingValue)}
            />
            <FaStar
              className="star inline justify-center"
              color={ratingValue <= (hover || rating) ? '#ffc107' : '#e4e5e9'}
              size={25}
              onMouseEnter={() => setHover(ratingValue)}
              onMouseLeave={() => setHover(null)}
            />
          </label>
        );
      })}
      <p className='m-10 justify-center'>
        {whichemoji(rating)}
      </p>
    </div>


        <button
          type="submit"
          className="hover:bg-emerald-700 ease-in duration-300 shadow-lg shadow-gray-800 row-span-1 bg-emerald-900 text-white font-bold py-2 px-4 rounded"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

function whichemoji(props) {
    if (props === 1) {
      return (
        <div>
          <p>‚òπÔ∏è</p>
        </div>
      );
    } 
    else if (props === 2) {
      return (
        <div>
          <p>üôÅ</p>
        </div>
      );
    }
    else if (props === 3) {
      return (
        <div>
          <p>üôÇ</p>
        </div>
      );
    }
    else if (props === 4) {
      return (
        <div>
          <p>üòä</p>
        </div>
      );
    }
    else if (props === 5) {
      return (
        <div>
          <p>ü§© Thank you</p>
        </div>
      );
    }
  }

export default Contact;
